    if pixel_color == 0:
        return False, 0

    left_is_white = (c == 0) or (grid[r, c-1] == 0)
    right_is_white = (c == cols - 1) or (grid[r, c+1] == 0)

    if left_is_white and right_is_white:
        return True, pixel_color
    else:
